(2, 92, 'BIAYA MASUK & PERKULIAHAN KELAS REGULER UNIVERSITAS BUDI LUHUR', 'biaya-masuk-perkuliahan-kelas-reguler-universitas-budi-luhur', '<p><img src=\"http://infopmb.budiluhur.ac.id/wp-content/uploads/2022/10/Flyer-2023-ok-2-1-scaled.jpg\" /></p>', '', 1, 2, '2023-04-11 16:05:08', 768, '', '2023-04-11 16:05:08', 768, NULL, NULL, '2023-04-11 16:05:08', NULL, '{\"image_intro\":\"\",\"image_intro_alt\":\"\",\"float_intro\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"float_fulltext\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":\"\",\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":\"\",\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":\"\",\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\",\"helix_ultimate_image\":\"\",\"helix_ultimate_image_alt_txt\":\"\",\"helix_ultimate_article_format\":\"standard\",\"helix_ultimate_audio\":\"\",\"helix_ultimate_gallery\":\"\",\"helix_ultimate_video\":\"\"}', 1, 3, '', '', 1, 0, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\"}', 0, '*', ''),
(3, 93, 'Visi dan Misi', 'visi-dan-misi', '<p><strong>VISI</strong></p>\r\n<ul>\r\n<li>Menjadi Universitas berwawasan global berbasis kewirausahaan, teknologi dan cerdas berbudiluhur</li>\r\n</ul>\r\n<p><strong>MISI</strong></p>\r\n<ul>\r\n<li>Menyelenggarakan pendidikan yang mengedepankan teknologi, ilmu pengetahuan, kewirausahaan dan kebudiluhuran;</li>\r\n<li>Menyelenggarakan penelitian yang menciptakan dampak sosial yang positif, terukur dan berkesinambungan;</li>\r\n<li>Berperan aktif dalam menyejahterakan kehidupan sosial masyarakat yang lebih baik dengan mengimplementasikan hasil penelitian;</li>\r\n<li>Berperan aktif dalam kegiatan sosial global yang bermanfaat bagi umat;</li>\r\n</ul>', '', 1, 2, '2023-04-11 16:06:29', 768, '', '2023-04-11 16:06:29', 768, NULL, NULL, '2023-04-11 16:06:29', NULL, '{\"image_intro\":\"\",\"image_intro_alt\":\"\",\"float_intro\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"float_fulltext\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":\"\",\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":\"\",\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":\"\",\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\",\"helix_ultimate_image\":\"\",\"helix_ultimate_image_alt_txt\":\"\",\"helix_ultimate_article_format\":\"standard\",\"helix_ultimate_audio\":\"\",\"helix_ultimate_gallery\":\"\",\"helix_ultimate_video\":\"\"}', 1, 2, '', '', 1, 0, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\"}', 0, '*', ''),
(4, 94, 'Kelas Karyawan Universitas Budi Luhur adalah solusi bila anda sudah bekerja dan ingin kuliah', 'kelas-karyawan-universitas-budi-luhur-adalah-solusi-bila-anda-sudah-bekerja-dan-ingin-kuliah', '<p>Kelas Karyawan Universitas Budi Luhur memberikan solusinya, segera daftarkan di Kelas Karyawan atau Program Kuliah Karyawan. Kuliah Kelas Karyawan diselenggarakan untuk menunjang karir dengan waktu kuliah yang tidak menganggu kesibukan pekerjaan anda. Kami adalah universitas swasta untuk kelas karyawan yang menerima juga mahasiswa pindahan dan alih jenjang.</p>\r\n<p>Kuliah Kelas Karyawan Budi Luhur menyelenggarakan pendidikan Strata 1 (S1) dan <a href=\"http://pascasarjana.budiluhur.ac.id/\">Strata 2 (S2)</a></p>\r\n<p>Fleksibiltas dan Fasilitas yang ada di Program Kelas Karyawan</p>\r\n<ul>\r\n<li>Sistem Kuliah Blended Learning (Tatap Muka dan e-Learning)</li>\r\n<li>Biaya Kuliah diangsur Perbulan</li>\r\n<li>Waktu Kuliah Fleksible agar tidak menganggu pekerjaan anda (kuliah sabtu malam minggu)<strong><br /></strong></li>\r\n<li>Sistem e-Learning yang memadai agar dapat belajar dimanapun dan kapanpun</li>\r\n<li>Kurikulum dirancang agar dapat lulus tepat waktu</li>\r\n<li>Tidak ada batasan umur dan tahun lulus pendidikan sebelumnya</li>\r\n<li>Fasilitas pendidikan yang lengkap</li>\r\n<li>Fasilitas olahraga yang lengkap</li>\r\n</ul>', '', 1, 2, '2023-04-11 16:07:46', 768, '', '2023-04-11 16:07:46', 768, NULL, NULL, '2023-04-11 16:07:46', NULL, '{\"image_intro\":\"\",\"image_intro_alt\":\"\",\"float_intro\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"float_fulltext\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":\"\",\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":\"\",\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":\"\",\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\",\"helix_ultimate_image\":\"\",\"helix_ultimate_image_alt_txt\":\"\",\"helix_ultimate_article_format\":\"standard\",\"helix_ultimate_audio\":\"\",\"helix_ultimate_gallery\":\"\",\"helix_ultimate_video\":\"\"}', 1, 1, '', '', 1, 0, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\"}', 0, '*', ''),
